MusicManager.prototype.manageSpotLine = function(){
	var self = this;
	for(i=0;i<self.spots.length;i++){
		if(self.testCicle-self.spots[i].lastTime>self.spots[i].min_interval){
			self.spots[i].lastTime = self.testCicle;
			self.spotLine.push(self.spots[i]);
		}
	}
	console.log("self.spotLine");
	console.log(self.spotLine);
}



MusicManager.prototype.getLocalList = function(callback){ //Pega a lista atual SEM ATUALIZAR NADA do servidor e põe pra jogo.
	var self = this;
	self.customList = [];
	self.customListIndex = [];
	// Query out the data
	this.db.transaction(function (tx) {
	  tx.executeSql('SELECT * FROM musicas WHERE playlist <> "chamadas"', [], function (tx, results) {
	  	var ret = [];
	    var len = results.rows.length, i;
	    for (i = 0; i < len; i++) {
			var mus = results.rows.item(i);	    	
	    	mus.file = (mus.path).split('/').pop();
	      	ret.push(mus);
			self.customList.push(mus);
			self.customListIndex.push(mus.audioid);
	    }
	    if(callback)callback(ret);
	  }, self.SQLError);
	});
}


